import React, { useState } from "react";
import './Rating.css'

const StarRating = () => {
    const [rating, setRating] = useState(0);
    return (
      <div className="star-rating">
        {[...Array(3)].map((star, index) => {
          index += 1;
          return (
            <button
              type="button"
              key={index}
              className={index >= rating ? "on" : "off"}
              onClick={() => setRating(index)}
            >
              <span className="star">&#9733;</span>
            </button>
          );
        })}

        {[...Array(2)].map((star, index) => {
                index += 1;
                return (
                    <button
                    type="button"
                    key={index}
                    className={index <= rating ? "on" : "off"}
                    onClick={() => setRating(index)}
                    >
                    <span className="star">&#9733;</span>
                    </button>
                );
        })}
      </div>
    );
  };

  const StarRatingTamil = () => {
    const [rating, setRating] = useState(0);
    return (
      <div className="star-rating">
        {[...Array(3)].map((star, index) => {
          index += 1;
          return (
            <button
              type="button"
              key={index}
              className={index >= rating ? "on" : "off"}
              onClick={() => setRating(index)}
            >
              <span className="star">&#9733;</span>
            </button>
          );
        })}

        {[...Array(2)].map((star, index) => {
                index += 1;
                return (
                    <button
                    type="button"
                    key={index}
                    className={index <= rating ? "on" : "off"}
                    onClick={() => setRating(index)}
                    >
                    <span className="star">&#9733;</span>
                    </button>
                );
        })}
      </div>
    );
  };



module.exports = {
    StarRating,
    StarRatingTamil : StarRatingTamil
}
